def count_floors(str)
  str.chars.inject(0) do |sum, char|
    if char == "("
      sum += 1
    elsif char == ")"
      sum -= 1
    end
  end
end

def find_basement(str)
  tally = 0
  str.chars.each_with_index do |char, index|
    if char == "("
      tally += 1
    elsif char == ")"
      tally -= 1
    end
    return index + 1 if tally == -1
  end
end

